mapper.update:
update shop
set viewcnt = viewcnt + 1
where pid=#{pid}

mapper.insert:
insert into shop(productId, title, image, lprice,
maker)
values(#{productId}, #{title}, #{image}, #{lprice}, #{maker});

mapper.select:
select *, date_format(regdate, '%Y-%m-%d %T') fmtdate, format(lprice, 0) fmtprice
from shop
where pid = #{pid}

mapper.update:
		update shop
      	set fcnt = fcnt + #{amount}
      	where pid = #{pid}

update shop
set title=#{title}, lprice=#{lprice},
maker = #{maker}
where pid = #{pid}

update shop
set image = #{image}
where pid = #{pid}
